#!/usr/bin/env python3
import rclpy
from rclpy.node import Node
from sensor_msgs.msg import Image
from std_msgs.msg import String
from cv_bridge import CvBridge, CvBridgeError
import cv2
import numpy as np


class VisionProcessing(Node):
    def __init__(self):
        super().__init__('vision_processing')

        # CV bridge
        self.bridge = CvBridge()

        # Subscribers
        self.subscription = self.create_subscription(
            Image,
            '/image_raw',
            self.image_callback,
            10
        )

        # Publishers
        self.state_publisher = self.create_publisher(String, '/kernel_state', 10)
        self.debug_image_pub = self.create_publisher(Image, '/receive_kernel_debug', 10)

        # Processing parameters
        self.center_circle_radius = 50
        self.blur_ksize = 9
        self.brightness_factor = 1

        # HSV range for yellow/white detection
        self.lower_yellow = np.array([15, 50, 150])
        self.upper_yellow = np.array([45, 255, 255])

        # Initialization / calibration
        self.ref_frame = None
        self.init_diffs = []
        self.init_frame_count = 5  # number of frames to average during "initializing"
        self.avg_diff = None
        self.state = "initializing"

        self.get_logger().info("üëÅÔ∏è VisionProcessing node started (state: initializing)")

    def image_callback(self, msg: Image):
        try:
            # Convert ROS image to OpenCV
            cv_image = self.bridge.imgmsg_to_cv2(msg, desired_encoding='bgr8')
            height, width = cv_image.shape[:2]

            # === Step 1: Mask ===
            mask = np.zeros((height, width), dtype=np.uint8)
            cv2.circle(mask, (width // 2, height // 2), self.center_circle_radius, 255, -1)
            masked_image = cv2.bitwise_and(cv_image, cv_image, mask=mask)

            # === Step 2: Yellow/white filter ===
            hsv = cv2.cvtColor(masked_image, cv2.COLOR_BGR2HSV)
            yellow_mask = cv2.inRange(hsv, self.lower_yellow, self.upper_yellow)
            yellow_filtered = cv2.bitwise_and(masked_image, masked_image, mask=yellow_mask)

            # === Step 3: Blur and brightness ===
            blurred = cv2.medianBlur(yellow_filtered, self.blur_ksize)
            bright = cv2.convertScaleAbs(blurred, alpha=self.brightness_factor, beta=0)
            gray = cv2.cvtColor(bright, cv2.COLOR_BGR2GRAY)

            # === Step 4: Reference frame handling ===
            if self.ref_frame is None:
                self.ref_frame = gray.copy()
                self.get_logger().info("Reference frame captured")
                return

            diff = cv2.absdiff(gray, self.ref_frame)
            diff_value = np.sum(diff)

            # === Step 5: State machine ===
            if self.state == "initializing":
                self.init_diffs.append(diff_value)
                self.get_logger().info(f"Initializing... frame {len(self.init_diffs)}/{self.init_frame_count}: {diff_value}")
                if len(self.init_diffs) >= self.init_frame_count:
                    self.avg_diff = np.mean(self.init_diffs)
                    self.state = "wait_for_kernel"
                    self.get_logger().info(f"Initialization complete ‚úÖ avg diff = {self.avg_diff}")
                    self.publish_state("wait_for_kernel")

            elif self.state == "wait_for_kernel":
                # Count kernels using watershed
                kernel_count, debug_image = self.count_kernels_watershed(gray)

                # Overlay kernel count text
                cv2.putText(debug_image, f"Kernels: {kernel_count}", (10, 30),
                            cv2.FONT_HERSHEY_SIMPLEX, 1, (0, 255, 0), 2)

                # Publish debug image
                debug_msg = self.bridge.cv2_to_imgmsg(debug_image, encoding='bgr8')
                self.debug_image_pub.publish(debug_msg)

                # Decide state based on kernel count
                if kernel_count == 0:
                    self.publish_state("wait_for_kernel")
                    self.get_logger().info("Waiting for kernel...")
                elif kernel_count == 1:
                    self.publish_state("single_kernel_detected")
                    self.get_logger().info("üåΩ Kernel detected! üåΩ")
                else:
                    self.publish_state("excess_kernel_detected")
                    self.get_logger().info("‚ö†Ô∏è EXCESS Kernel detected")

            elif self.state == "single_kernel_detected":
                self.publish_state("single_kernel_detected")
                pass

            elif self.state == "excess_kernel_detected":
                self.publish_state("excess_kernel_detected")
                pass

        except CvBridgeError as e:
            self.get_logger().error(f"CV Bridge Error: {e}")

    def count_kernels_watershed(self, foreground_gray):
        """
        Apply watershed to separate touching kernels and count them.
        foreground_gray: binary or pre-processed gray image of kernels
        Returns: kernel_count, colored_debug_image
        """
        # Threshold to get binary image
        _, binary = cv2.threshold(foreground_gray, 10, 255, cv2.THRESH_BINARY)

        # Morphological opening to remove noise
        kernel = np.ones((3,3), np.uint8)
        opening = cv2.morphologyEx(binary, cv2.MORPH_OPEN, kernel, iterations=2)

        # Sure background
        sure_bg = cv2.dilate(opening, kernel, iterations=3)

        # Distance transform to find centers
        dist_transform = cv2.distanceTransform(opening, cv2.DIST_L2, 5)
        _, sure_fg = cv2.threshold(dist_transform, 0.2 * dist_transform.max(), 255, 0)
        sure_fg = np.uint8(sure_fg)

        # Unknown region
        unknown = cv2.subtract(sure_bg, sure_fg)

        # Marker labeling
        _, markers = cv2.connectedComponents(sure_fg)
        markers = markers + 1
        markers[unknown == 255] = 0

        # Watershed
        debug_color = cv2.cvtColor(foreground_gray, cv2.COLOR_GRAY2BGR)
        cv2.watershed(debug_color, markers)

        # Mark boundaries in red
        debug_color[markers == -1] = [0, 0, 255]

        # Count kernels
        kernel_count = len(np.unique(markers)) - 2  # background=1, boundary=-1

        return kernel_count, debug_color

    def publish_state(self, state_str):
        msg = String()
        msg.data = state_str
        self.state_publisher.publish(msg)


def main(args=None):
    rclpy.init(args=args)
    node = VisionProcessing()
    try:
        rclpy.spin(node)
    except KeyboardInterrupt:
        pass
    finally:
        node.destroy_node()
        rclpy.shutdown()


if __name__ == '__main__':
    main()
